const menuBtn=document.querySelector(".burger"),menuMobile=document.querySelector(".menu"),body=document.querySelector("body");menuBtn.addEventListener("click",(()=>{menuMobile.classList.toggle("open"),body.classList.toggle("lock")}));const cg=document.querySelector(".content__inner"),style=document.createElement("style");document.head.appendChild(style);const hideHalfEmptyColumns=function(){const e=Math.round(cg.offsetWidth/160),n=cg.childElementCount,t=e*Math.floor(n/e);style.innerHTML=`\n            .content__inner>div:nth-child(n + ${t+1}) {\n                display: none;\n            }`};hideHalfEmptyColumns(),window.addEventListener("resize",(function(e){hideHalfEmptyColumns()}));